mapper.select:
select * from v_log
where type >= 0

    and username LIKE CONCAT('%',#{params.username},'%')


    and type = #{params.type}


    and create_time >= #{params.createTimeStart}


    and create_time <= #{params.createTimeEnd}

select * from v_log
where 1=1

    and username LIKE CONCAT('%',#{params.username},'%')


    and type = #{params.type}


    and create_time >= #{params.createTimeStart}


    and create_time <= #{params.createTimeEnd}

select count(1) from v_log
where type >= 0

    and username LIKE CONCAT('%',#{params.username},'%')


    and type = #{params.type}


    and create_time >= #{params.createTimeStart}


    and create_time <= #{params.createTimeEnd}

select * from v_log
where 1=1

    and username LIKE CONCAT('%',#{params.username},'%')


    and type = #{params.type}


    and create_time >= #{params.createTimeStart}


    and create_time <= #{params.createTimeEnd}

limit #{offset},#{limit}
